### 5. **src/components/Reports.tsx**
```typescript
import { useMemo } from 'react';
import { FileText, Share2, Mail, MessageCircle, Trash2, Calendar } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { toast } from 'sonner';
import { useFeeding } from '../context/FeedingContext';

const Reports = () => {
  const { sessions, profile, clearSessions } = useFeeding();

  const stats = useMemo(() => {
    const totalSessions = sessions.length;
    const totalTime = sessions.reduce((acc, session) => acc + session.duration, 0);
    const leftSessions = sessions.filter(s => s.side === 'left').length;
    const rightSessions = sessions.filter(s => s.side === 'right').length;
    const avgDuration = totalSessions > 0 ? Math.round(totalTime / totalSessions) : 0;

    return {
      totalSessions,
      totalTime,
      leftSessions,
      rightSessions,
      avgDuration,
    };
  }, [sessions]);

  const formatTime = (totalSeconds: number) => {
    const hours = Math.floor(totalSeconds / 3600);
    const mins = Math.floor((totalSeconds % 3600) / 60);
    const secs = totalSeconds % 60;

    if (hours > 0) {
      return `${hours}h ${mins}m ${secs}s`;
    }
    return `${mins}m ${secs}s`;
  };

  const formatDate = (date: Date) => {
    return new Date(date).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const generateReport = () => {
    if (sessions.length === 0) {
      toast.error('Nenhuma sess√£o para gerar relat√≥rio');
      return '';
    }

    const babyName = profile.babyName || 'Beb√™';
    const motherName = profile.motherName || 'Mam√£e';

    let report = `üìä RELAT√ìRIO DE MAMADAS - ${babyName}\
`;
    report += `Mam√£e: ${motherName}\
\
`;
    report += `üìà RESUMO:\
`;
    report += `‚Ä¢ Total de mamadas: ${stats.totalSessions}\
`;
    report += `‚Ä¢ Tempo total: ${formatTime(stats.totalTime)}\
`;
    report += `‚Ä¢ Tempo m√©dio: ${formatTime(stats.avgDuration)}\
`;
    report += `‚Ä¢ Seio esquerdo: ${stats.leftSessions} vezes\
`;
    report += `‚Ä¢ Seio direito: ${stats.rightSessions} vezes\
\
`;

    report += `üìã HIST√ìRICO DAS √öLTIMAS MAMADAS:\
`;

    const recentSessions = sessions.slice(-10).reverse();
    recentSessions.forEach((session, index) => {
      const sideText = session.side === 'left' ? 'Esquerdo' : 'Direito';
      report += `${index + 1}. ${formatDate(session.startTime)} - Seio ${sideText} - ${formatTime(session.duration)}\
`;
    });

    report += `\
üíú Gerado pelo Mamada Tracker`;
    report += `\
Desenvolvido por @fonoizabeldias`;

    return report;
  };

  const shareToWhatsApp = () => {
    const report = generateReport();
    if (report) {
      const url = `https://wa.me/?text=${encodeURIComponent(report)}`;
      window.open(url, '_blank');
      toast.success('Abrindo WhatsApp para compartilhar');
    }
  };

  const shareToEmail = () => {
    const report = generateReport();
    if (report) {
      const subject = `Relat√≥rio de Mamadas - ${profile.babyName || 'Beb√™'}`;
      const body = report.replace(/\
/g, '%0A');
      const url = `mailto:?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
      window.open(url, '_blank');
      toast.success('Abrindo cliente de email');
    }
  };

  const handleClearSessions = () => {
    if (window.confirm('Tem certeza que deseja apagar todos os registros? Esta a√ß√£o n√£o pode ser desfeita.')) {
      clearSessions();
      toast.success('Todos os registros foram apagados');
    }
  };

  return (
    <div className="max-w-2xl mx-auto space-y-6">
      <Card>
        <CardHeader className="text-center">
          <CardTitle className="text-2xl flex items-center justify-center gap-2">
            <FileText className="w-6 h-6" />
            Relat√≥rios
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {sessions.length === 0 ? (
            <div className="text-center py-8">
              <Calendar className="w-12 h-12 mx-auto text-muted-foreground mb-4" />
              <p className="text-muted-foreground">
