            <Timer className="w-4 h-4 absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-primary" />
          </div>
          <h1 className="text-2xl font-bold">Mamada Tracker</h1>
        </div>
      </div>
    </header>
  );
};

export default Header;

const Footer = () => {
  return (
    <footer className="bg-muted py-4 mt-8">
      <div className="container mx-auto px-4 text-center">
        <p className="text-sm text-muted-foreground">
          Desenvolvido por{' '}
          <span className="font-semibold text-primary">@fonoizabeldias</span>{' '}
          com üíú para todas as mam√£es
        </p>
      </div>
    </footer>
  );
};

export default Footer;

import { useLocation, Link } from 'react-router-dom';
import { Timer, User, FileText } from 'lucide-react';
import { Button } from '@/components/ui/button';

const Navigation = () => {
  const location = useLocation();

  const navItems = [
    { path: '/', icon: Timer, label: 'Timer' },
    { path: '/profile', icon: User, label: 'Perfil' },
    { path: '/reports', icon: FileText, label: 'Relat√≥rios' },
  ];

  return (
    <nav className="bg-card border-t border-border py-2">
      <div className="container mx-auto px-4">
        <div className="flex justify-around">
          {navItems.map(({ path, icon: Icon, label }) => (
            <Link key={path} to={path}>
              <Button
                variant={location.pathname === path ? 'default' : 'ghost'}
                className="flex flex-col items-center gap-1 h-auto py-2"
