import { useState, useEffect, useRef } from 'react';
import { Play, Pause, Square, RotateCcw } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { toast } from 'sonner';
import { useFeeding } from '../context/FeedingContext';

const Timer = () => {
  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [seconds, setSeconds] = useState(0);
  const [selectedSide, setSelectedSide] = useState<'left' | 'right' | null>(null);
  const [sessionStartTime, setSessionStartTime] = useState<Date | null>(null);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const { addSession } = useFeeding();

  useEffect(() => {
    if (isRunning && !isPaused) {
      intervalRef.current = setInterval(() => {
        setSeconds(prev => prev + 1);
      }, 1000);
    } else {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isRunning, isPaused]);

  const formatTime = (totalSeconds: number) => {
    const mins = Math.floor(totalSeconds / 60);
    const secs = totalSeconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const startTimer = (side: 'left' | 'right') => {
    if (!isRunning) {
      setSelectedSide(side);
      setSessionStartTime(new Date());
      setIsRunning(true);
      setIsPaused(false);
      toast.success(`Iniciando mamada no seio ${side === 'left' ? 'esquerdo' : 'direito'}`);
    }
  };

  const pauseTimer = () => {
    setIsPaused(true);
    toast.info('Timer pausado');
  };

  const resumeTimer = () => {
    setIsPaused(false);
    toast.info('Timer retomado');
  };

  const stopTimer = () => {
    if (isRunning && sessionStartTime && selectedSide && seconds > 0) {
      const endTime = new Date();
      const session = {
        id: Date.now().toString(),
        side: selectedSide,
        startTime: sessionStartTime,
        endTime,
        duration: seconds,
      };
      addSession(session);
      toast.success(`Mamada finalizada! Dura√ß√£o: ${formatTime(seconds)}`);
    }

    resetTimer();
  };

  const resetTimer = () => {
    setIsRunning(false);
    setIsPaused(false);
    setSeconds(0);
    setSelectedSide(null);
    setSessionStartTime(null);
  };

  return (
    <div className="max-w-md mx-auto space-y-6">
      <Card>
        <CardHeader className="text-center">
          <CardTitle className="text-2xl">Cron√¥metro de Mamada</CardTitle>
          {selectedSide && (
            <Badge variant="secondary" className="mx-auto">
              Seio {selectedSide === 'left' ? 'Esquerdo' : 'Direito'}
            </Badge>
          )}
        </CardHeader>
        <CardContent className="text-center space-y-6">
          <div className="text-6xl font-mono font-bold text-primary">
            {formatTime(seconds)}
          </div>

          {!isRunning ? (
            <div className="space-y-4">
              <p className="text-muted-foreground">Selecione o seio para come√ßar:</p>
              <div className="flex gap-4 justify-center">
                <Button
                  onClick={() => startTimer('left')}
                  className="flex-1 h-16 text-lg"
                  variant="outline"
                >
                  Seio Esquerdo
                </Button>
                <Button
                  onClick={() => startTimer('right')}
                  className="flex-1 h-16 text-lg"
                  variant="outline"
                >
                  Seio Direito
                </Button>
              </div>
            </div>
          ) : (
            <div className="flex gap-3 justify-center">
              {!isPaused ? (
                <Button onClick={pauseTimer} size="lg" variant="outline">
                  <Pause className="w-5 h-5 mr-2" />
                  Pausar
                </Button>
              ) : (
                <Button onClick={resumeTimer} size="lg">
                  <Play className="w-5 h-5 mr-2" />
                  Continuar
                </Button>
              )}

              <Button onClick={stopTimer} size="lg" variant="destructive">
                <Square className="w-5 h-5 mr-2" />
                Finalizar
              </Button>

              <Button onClick={resetTimer} size="lg" variant="outline">
                <RotateCcw className="w-5 h-5 mr-2" />
                Reset
              </Button>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default Timer;

import { useState } from 'react';
import { Save, User, Baby } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { toast } from 'sonner';
import { useFeeding } from '../context/FeedingContext';

const Profile = () => {
  const { profile, setProfile } = useFeeding();
  const [motherName, setMotherName] = useState(profile.motherName);
  const [babyName, setBabyName] = useState(profile.babyName);

  const handleSave = () => {
    setProfile({ motherName, babyName });
    toast.success('Perfil salvo com sucesso!');
  };

  return (
    <div className="max-w-md mx-auto">
      <Card>
        <CardHeader className="text-center">
          <CardTitle className="text-2xl flex items-center justify-center gap-2">
            <User className="w-6 h-6" />
            Perfil
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="space-y-2">
            <Label htmlFor="motherName" className="flex items-center gap-2">
              <User className="w-4 h-4" />
              Nome da Mam√£e
            </Label>
            <Input
              id="motherName"
              type="text"
              placeholder="Digite seu nome"
              value={motherName}
              onChange={(e) => setMotherName(e.target.value)}
              className="text-lg"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="babyName" className="flex items-center gap-2">
              <Baby className="w-4 h-4" />
              Nome do Beb√™
            </Label>
            <Input
              id="babyName"
              type="text"
              placeholder="Digite o nome do beb√™"
              value={babyName}
              onChange={(e) => setBabyName(e.target.value)}
              className="text-lg"
            />
          </div>

          <Button onClick={handleSave} className="w-full" size="lg">
            <Save className="w-5 h-5 mr-2" />
            Salvar Perfil
          </Button>

          {profile.motherName && profile.babyName && (
            <div className="mt-6 p-4 bg-accent rounded-lg">
              <h3 className="font-semibold text-center text-primary mb-2">
                Perfil Atual
              </h3>
              <div className="text-center space-y-1">
                <p className="text-sm">
                  <span className="font-medium">Mam√£e:</span> {profile.motherName}
                </p>
                <p className="text-sm">
                  <span className="font-medium">Beb√™:</span> {profile.babyName}
                </p>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default Profile;

import { useMemo } from 'react';
import { FileText, Share2, Mail, MessageCircle, Trash2, Calendar } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { toast } from 'sonner';
import { useFeeding } from '../context/FeedingContext';

const Reports = () => {
  const { sessions, profile, clearSessions } = useFeeding();

  const stats = useMemo(() => {
    const totalSessions = sessions.length;
    const totalTime = sessions.reduce((acc, session) => acc + session.duration, 0);
    const leftSessions = sessions.filter(s => s.side === 'left').length;
    const rightSessions = sessions.filter(s => s.side === 'right').length;
    const avgDuration = totalSessions > 0 ? Math.round(totalTime / totalSessions) : 0;

    return {
      totalSessions,
      totalTime,
      leftSessions,
      rightSessions,
      avgDuration,
    };
  }, [sessions]);

  const formatTime = (totalSeconds: number) => {
    const hours = Math.floor(totalSeconds / 3600);
    const mins = Math.floor((totalSeconds % 3600) / 60);
    const secs = totalSeconds % 60;

    if (hours > 0) {
      return `${hours}h ${mins}m ${secs}s`;
    }
    return `${mins}m ${secs}s`;
  };

  const formatDate = (date: Date) => {
    return new Date(date).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const generateReport = () => {
    if (sessions.length === 0) {
      toast.error('Nenhuma sess√£o para gerar relat√≥rio');
      return '';
    }

    const babyName = profile.babyName || 'Beb√™';
    const motherName = profile.motherName || 'Mam√£e';

    let report = `üìä RELAT√ìRIO DE MAMADAS - ${babyName}\\n`;
    report += `Mam√£e: ${motherName}\\n\\n`;
    report += `üìà RESUMO:\\n`;
    report += `‚Ä¢ Total de mamadas: ${stats.totalSessions}\\n`;
    report += `‚Ä¢ Tempo total: ${formatTime(stats.totalTime)}\\n`;
    report += `‚Ä¢ Tempo m√©dio: ${formatTime(stats.avgDuration)}\\n`;
    report += `‚Ä¢ Seio esquerdo: ${stats.leftSessions} vezes\\n`;
    report += `‚Ä¢ Seio direito: ${stats.rightSessions} vezes\\n\\n`;

    report += `üìã HIST√ìRICO DAS √öLTIMAS MAMADAS:\\n`;

    const recentSessions = sessions.slice(-10).reverse();
    recentSessions.forEach((session, index) => {
      const sideText = session.side === 'left' ? 'Esquerdo' : 'Direito';
      report += `${index + 1}. ${formatDate(session.startTime)} - Seio ${sideText} - ${formatTime(session.duration)}\\n`;
    });

    report += `\\nüíú Gerado pelo Mamada Tracker`;
    report += `\\nDesenvolvido por @fonoizabeldias`;

    return report;
  };

  const shareToWhatsApp = () => {
    const report = generateReport();
    if (report) {
      const url = `https://wa.me/?text=${encodeURIComponent(report)}`;
      window.open(url, '_blank');
      toast.success('Abrindo WhatsApp para compartilhar');
    }
  };

  const shareToEmail = () => {
    const report = generateReport();
    if (report) {
      const subject = `Relat√≥rio de Mamadas - ${profile.babyName || 'Beb√™'}`;
      const body = report.replace(/\\n/g, '%0A');
      const url = `mailto:?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
      window.open(url, '_blank');
      toast.success('Abrindo cliente de email');
    }
  };

  const handleClearSessions = () => {
    if (window.confirm('Tem certeza que deseja apagar todos os registros? Esta a√ß√£o n√£o pode ser desfeita.')) {
      clearSessions();
      toast.success('Todos os registros foram apagados');
    }
  };

  return (
    <div className="max-w-2xl mx-auto space-y-6">
      <Card>
        <CardHeader className="text-center">
          <CardTitle className="text-2xl flex items-center justify-center gap-2">
            <FileText className="w-6 h-6" />
            Relat√≥rios
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {sessions.length === 0 ? (
            <div className="text-center py-8">
              <Calendar className="w-12 h-12 mx-auto text-muted-foreground mb-4" />
              <p className="text-muted-foreground">
                Nenhuma mamada registrada ainda.
                <br />
                Comece usando o timer para ver seus relat√≥rios aqui!
              </p>
            </div>
          ) : (
            <>
              {/* Estat√≠sticas */}
              <div className="grid grid-cols-2 gap-4">
                <div className="text-center p-4 bg-accent rounded-lg">
                  <div className="text-2xl font-bold text-primary">{stats.totalSessions}</div>
                  <div className="text-sm text-muted-foreground">Total de Mamadas</div>
                </div>
                <div className="text-center p-4 bg-accent rounded-lg">
                  <div className="text-2xl font-bold text-primary">{formatTime(stats.totalTime)}</div>
                  <div className="text-sm text-muted-foreground">Tempo Total</div>
                </div>
                <div className="text-center p-4 bg-accent rounded-lg">
                  <div className="text-2xl font-bold text-primary">{stats.leftSessions}</div>
                  <div className="text-sm text-muted-foreground">Seio Esquerdo</div>
                </div>
                <div className="text-center p-4 bg-accent rounded-lg">
                  <div className="text-2xl font-bold text-primary">{stats.rightSessions}</div>
                  <div className="text-sm text-muted-foreground">Seio Direito</div>
                </div>
              </div>

              <Separator />

              {/* Hist√≥rico recente */}
              <div>
                <h3 className="font-semibold mb-3">√öltimas Mamadas</h3>
                <div className="space-y-2 max-h-60 overflow-y-auto">
                  {sessions.slice(-5).reverse().map((session) => (
                    <div key={session.id} className="flex items-center justify-between p-3 bg-muted rounded-lg">
                      <div>
                        <div className="font-medium text-sm">
                          {formatDate(session.startTime)}
                        </div>
                        <div className="text-xs text-muted-foreground">
                          {formatTime(session.duration)}
                        </div>
                      </div>
                      <Badge variant={session.side === 'left' ? 'secondary' : 'outline'}>
                        {session.side === 'left' ? 'Esquerdo' : 'Direito'}
                      </Badge>
                    </div>
                  ))}
                </div>
              </div>

              <Separator />

              {/* Bot√µes de a√ß√£o */}
              <div className="space-y-3">
                <div className="grid grid-cols-2 gap-3">
                  <Button onClick={shareToWhatsApp} className="flex items-center gap-2">
                    <MessageCircle className="w-4 h-4" />
                    WhatsApp
                  </Button>
                  <Button onClick={shareToEmail} variant="outline" className="flex items-center gap-2">
                    <Mail className="w-4 h-4" />
                    Email
                  </Button>
                </div>

                <Button 
                  onClick={handleClearSessions} 
                  variant="destructive" 
                  className="w-full"
                  size="sm"
                >
                  <Trash2 className="w-4 h-4 mr-2" />
                  Apagar Todos os Registros
                </Button>
              </div>
            </>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default Reports;

