export default App;

import React, { createContext, useContext, useState, useEffect } from 'react';

interface FeedingSession {
  id: string;
  side: 'left' | 'right';
  startTime: Date;
  endTime: Date;
  duration: number; // in seconds
}

interface Profile {
  motherName: string;
  babyName: string;
}

interface FeedingContextType {
  profile: Profile;
  setProfile: (profile: Profile) => void;
  sessions: FeedingSession[];
  addSession: (session: FeedingSession) => void;
  clearSessions: () => void;
}

const FeedingContext = createContext<FeedingContextType | undefined>(undefined);

export const FeedingProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [profile, setProfile] = useState<Profile>(() => {
    const saved = localStorage.getItem('mamada-profile');
    return saved ? JSON.parse(saved) : { motherName: '', babyName: '' };
  });

  const [sessions, setSessions] = useState<FeedingSession[]>(() => {
    const saved = localStorage.getItem('mamada-sessions');
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    localStorage.setItem('mamada-profile', JSON.stringify(profile));
  }, [profile]);

  useEffect(() => {
    localStorage.setItem('mamada-sessions', JSON.stringify(sessions));
  }, [sessions]);

  const addSession = (session: FeedingSession) => {
    setSessions(prev => [...prev, session]);
  };

  const clearSessions = () => {
    setSessions([]);
    localStorage.removeItem('mamada-sessions');
  };

  return (
    <FeedingContext.Provider value={{ profile, setProfile, sessions, addSession, clearSessions }}>
      {children}
    </FeedingContext.Provider>
  );
};

export const useFeeding = () => {
  const context = useContext(FeedingContext);
  if (!context) {
    throw new Error('useFeeding must be used within FeedingProvider');
  }
  return context;
};

