                  className="flex-1 h-16 text-lg"
                  variant={selectedSide === 'left' ? 'default' : 'outline'}
                >
                  Seio Esquerdo
                </Button>
                <Button
                  onClick={() => setSelectedSide('right')}
                  className="flex-1 h-16 text-lg"
                  variant={selectedSide === 'right' ? 'default' : 'outline'}
                >
                  Seio Direito
                </Button>
              </div>

              {selectedSide && (
                <Button 
                  onClick={startTimer} 
                  size="lg" 
                  className="w-full h-14 text-lg"
                >
                  <Play className="w-6 h-6 mr-2" />
                  Iniciar Cron√¥metro
                </Button>
              )}
            </div>
          ) : (
            <div className="space-y-3">
              {!isPaused ? (
                <Button onClick={pauseTimer} size="lg" variant="outline" className="w-full">
                  <Pause className="w-5 h-5 mr-2" />
                  Pausar
                </Button>
              ) : (
                <Button onClick={resumeTimer} size="lg" className="w-full">
                  <Play className="w-5 h-5 mr-2" />
                  Continuar
                </Button>
              )}

              <div className="grid grid-cols-2 gap-3">
                <Button onClick={stopTimer} size="lg" variant="destructive">
                  <Square className="w-5 h-5 mr-2" />
                  Finalizar
                </Button>

                <Button onClick={resetTimer} size="lg" variant="outline">
                  <RotateCcw className="w-5 h-5 mr-2" />
                  Reset
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default Timer;

import { useLocation, Link } from 'react-router-dom';
import { Timer, User, FileText, AlertCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useFeeding } from '../context/FeedingContext';

const Navigation = () => {
  const location = useLocation();
  const { profile } = useFeeding();

  // Verifica se o perfil est√° completo
  const isProfileComplete = profile.motherName && profile.babyName;

  const navItems = [
    { path: '/', icon: Timer, label: 'Timer' },
    { path: '/profile', icon: isProfileComplete ? User : AlertCircle, label: 'Perfil' },
    { path: '/reports', icon: FileText, label: 'Relat√≥rios' },
  ];

  return (
    <nav className="bg-card border-t border-border py-2">
      <div className="container mx-auto px-4">
        <div className="flex justify-around">
          {navItems.map(({ path, icon: Icon, label }) => (
            <Link key={path} to={path}>
              <Button
                variant={location.pathname === path ? 'default' : 'ghost'}
                className={`flex flex-col items-center gap-1 h-auto py-2 ${
                  path === '/profile' && !isProfileComplete ? 'text-destructive' : ''
                }`}
                size="sm"
              >
                <Icon className="w-5 h-5" />
                <span className="text-xs">{label}</span>
              </Button>
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default Navigation;

