```typescript
import { useState, useEffect, useRef } from 'react';
import { Play, Pause, Square, RotateCcw } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { toast } from 'sonner';
import { useFeeding } from '../context/FeedingContext';
import { Link } from 'react-router-dom';

const Timer = () => {
  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [seconds, setSeconds] = useState(0);
  const [selectedSide, setSelectedSide] = useState<'left' | 'right' | null>(null);
  const [sessionStartTime, setSessionStartTime] = useState<Date | null>(null);
  const intervalRef = useRef<number | null>(null);
  const { addSession, profile } = useFeeding();

  // Verifica se o perfil est√° preenchido
  const isProfileComplete = profile.motherName && profile.babyName;

  useEffect(() => {
    if (isRunning && !isPaused) {
      intervalRef.current = window.setInterval(() => {
        setSeconds(prev => prev + 1);
      }, 1000);
    } else {
      if (intervalRef.current) {
        window.clearInterval(intervalRef.current);
      }
    }

    return () => {
      if (intervalRef.current) {
        window.clearInterval(intervalRef.current);
      }
    };
  }, [isRunning, isPaused]);

  const formatTime = (totalSeconds: number) => {
    const mins = Math.floor(totalSeconds / 60);
    const secs = totalSeconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const startTimer = () => {
    if (selectedSide && !isRunning) {
      setSessionStartTime(new Date());
      setIsRunning(true);
      setIsPaused(false);
      toast.success(`Iniciando mamada no seio ${selectedSide === 'left' ? 'esquerdo' : 'direito'}`);
    }
  };

  const pauseTimer = () => {
    setIsPaused(true);
    toast.info('Timer pausado');
  };

  const resumeTimer = () => {
    setIsPaused(false);
    toast.info('Timer retomado');
  };

  const stopTimer = () => {
    if (isRunning && sessionStartTime && selectedSide && seconds > 0) {
      const endTime = new Date();
      const session = {
        id: Date.now().toString(),
        side: selectedSide,
        startTime: sessionStartTime,
        endTime,
        duration: seconds,
      };
      addSession(session);
      toast.success(`Mamada finalizada! Dura√ß√£o: ${formatTime(seconds)}`);
    }

    resetTimer();
  };

  const resetTimer = () => {
    setIsRunning(false);
    setIsPaused(false);
    setSeconds(0);
    setSelectedSide(null);
    setSessionStartTime(null);
  };

  // Se o perfil n√£o estiver completo, mostra tela de aviso
  if (!isProfileComplete) {
    return (
      <div className="max-w-md mx-auto space-y-6">
        <Card>
          <CardHeader className="text-center">
            <CardTitle className="text-2xl">Perfil Necess√°rio</CardTitle>
          </CardHeader>
          <CardContent className="text-center space-y-4">
            <p className="text-muted-foreground">
              Para usar o cron√¥metro, voc√™ precisa preencher seu perfil primeiro.
            </p>
            <Link to="/profile">
              <Button className="w-full" size="lg">
                Preencher Perfil
              </Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="max-w-md mx-auto space-y-6">
      <Card>
        <CardHeader className="text-center">
          <CardTitle className="text-2xl">Cron√¥metro de Mamada</CardTitle>
          {selectedSide && (
            <Badge variant="secondary" className="mx-auto">
              Seio {selectedSide === 'left' ? 'Esquerdo' : 'Direito'}
            </Badge>
          )}
        </CardHeader>
        <CardContent className="text-center space-y-6">
          {/* Mensagem personalizada */}
          <div className="bg-accent p-4 rounded-lg">
            <p className="text-primary font-medium">
              {profile.motherName} est√° pronta para amamentar {profile.babyName}?
            </p>
          </div>

          <div className="text-6xl font-mono font-bold text-primary">
            {formatTime(seconds)}
          </div>

          {!isRunning ? (
            <div className="space-y-4">
              <p className="text-muted-foreground">Selecione o seio:</p>
              <div className="flex gap-4 justify-center">
                <Button
                  onClick={() => setSelectedSide('left')}
                  className="flex-1 h-16 text-lg"
                  variant={selectedSide === 'left' ? 'default' : 'outline'}
                >
                  Seio Esquerdo
                </Button>
                <Button
                  onClick={() => setSelectedSide('right')}
                  className="flex-1 h-16 text-lg"
                  variant={selectedSide === 'right' ? 'default' : 'outline'}
                >
                  Seio Direito
                </Button>
              </div>

              {selectedSide && (
                <Button 
                  onClick={startTimer} 
                  size="lg" 
                  className="w-full h-14 text-lg"
                >
                  <Play className="w-6 h-6 mr-2" />
                  Iniciar Cron√¥metro
                </Button>
              )}
            </div>
          ) : (
            <div className="space-y-3">
              {!isPaused ? (
                <Button onClick={pauseTimer} size="lg" variant="outline" className="w-full">
                  <Pause className="w-5 h-5 mr-2" />
                  Pausar
                </Button>
              ) : (
                <Button onClick={resumeTimer} size="lg" className="w-full">
                  <Play className="w-5 h-5 mr-2" />
                  Continuar
                </Button>
              )}

              <div className="grid grid-cols-2 gap-3">
                <Button onClick={stopTimer} size="lg" variant="destructive">
                  <Square className="w-5 h-5 mr-2" />
                  Finalizar
                </Button>

                <Button onClick={resetTimer} size="lg" variant="outline">
                  <RotateCcw className="w-5 h-5 mr-2" />
                  Reset
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default Timer;
```

